import React, { Component } from "react";


class Button extends Component {
    constructor(props) {
        super(props);
        this.state = {
            title: "Awesome"
        };
      }

    componentDidMount() {

    }

    componentWillUnmount() {

        this.setState({
            title: "Fuck"
        })

    }

    tick() {
        this.setState({
          title: "Clicked"
        });
      }

    render() {
        return(
            <button href={this.props.link}>{this.state.title}</button>
        );
    }

}

export default Button;
