import React, { Component } from "react";


class Button extends Component {
    constructor(props) {
        super(props);
        this.state = {
            title: "Awesome"
        };
      }

    componentDidMount() {
        this.timerID = setInterval(
            () => this.tick(),
            1000
          );
    }

    componentWillUnmount() {

        clearInterval(this.timerID);

    }

    tick() {
        this.setState({
          date: new Date()
        });
      }

    render() {
        return(
            <button href={this.props.link}>{this.state.title}</button>
        );
    }

}

export default Button;
